import React, { useState } from 'react';
import ReactMarkdown from 'react-markdown';
import './App.css';

function App() {
  const [messages, setMessages] = useState([
    { text: 'Hello, how can I help you today?', sender: 'bot' }
  ]);
  const [input, setInput] = useState('');

  const handleSend = async () => {
    // 1. Do nothing if the input is empty
    if (input.trim() === '') return;

    // 2. Create the user's message and add it to the chat window
    const userMessage = { text: input, sender: 'user' };
    setMessages(prevMessages => [...prevMessages, userMessage]);

    // 3. Send the user's message to the backend
    try {
      const response = await fetch('http://localhost:5001/api/chat', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ message: input }), // Use the 'input' state directly
      });

      const data = await response.json();
      const botMessage = { text: data.reply, sender: 'bot' };

      // 4. Add the bot's response to the chat window
      setMessages(prevMessages => [...prevMessages, botMessage]);

    } catch (error) {
      console.error('Error fetching bot reply:', error);
      const errorMessage = { text: 'Sorry, I am having trouble connecting.', sender: 'bot' };
      setMessages(prevMessages => [...prevMessages, errorMessage]);
    }

    // 5. NOW, clear the input box after everything is done.
    setInput('');
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>BniChat Bot</h1>
      </header>
      <div className="chat-window">
        {messages.map((msg, index) => (
          <div key={index} className={`message ${msg.sender}`}>
            {msg.sender === 'bot' ? (
              <ReactMarkdown>{msg.text}</ReactMarkdown>
            ) : (
              <p>{msg.text}</p>
            )}
          </div>
        ))}
      </div>
      <div className="chat-input">
        <input
          type="text"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          onKeyPress={(e) => e.key === 'Enter' && handleSend()}
          placeholder="Ask a business-related query..."
        />
        <button onClick={handleSend}>Send</button>
      </div>
    </div>
  );
}

export default App;
