import React, { useState, useEffect } from 'react';
import ReactMarkdown from 'react-markdown';
import SpeechRecognition, { useSpeechRecognition } from 'react-speech-recognition';
import './App.css';

function App() {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');

  // --- Voice Recognition Hook Setup ---
  const {
    transcript,
    listening,
    resetTranscript,
    browserSupportsSpeechRecognition
  } = useSpeechRecognition();

  // --- FIX FOR SPEECH SYNTHESIS ---
  // "Wake up" the speech synthesis engine when the component loads
  useEffect(() => {
    const warmUpSpeechSynthesis = () => {
      window.speechSynthesis.getVoices();
    };
    warmUpSpeechSynthesis();
  }, []);
  // --- END OF FIX ---

  // Update the input field with the live transcript
  useEffect(() => {
    setInput(transcript);
  }, [transcript]);

  // Main Send Function (for both text and voice)
  const handleSend = async (messageToSend) => {
    // Clear the input and transcript right away
    setInput('');
    resetTranscript();

    const userMessage = { text: messageToSend, sender: 'user' };
    setMessages(prevMessages => [...prevMessages, userMessage]);

    try {
      const response = await fetch('http://localhost:5001/api/chat', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ message: messageToSend }),
      });

      const data = await response.json();
      const botMessage = { text: data.reply, sender: 'bot' };
      setMessages(prevMessages => [...prevMessages, botMessage]);

      // --- Text-to-Speech (Voice Output) with FIX ---
      window.speechSynthesis.cancel(); // Clear any previous utterances
      const utterance = new SpeechSynthesisUtterance(data.reply);
      window.speechSynthesis.speak(utterance);
      // --- End of Text-to-Speech ---

    } catch (error) {
      console.error('Error fetching bot reply:', error);
      const errorMessage = { text: 'Sorry, I am having trouble connecting.', sender: 'bot' };
      setMessages(prevMessages => [...prevMessages, errorMessage]);
    }
  };

  const handleTextSubmit = () => {
    if (input.trim() === '') return;
    handleSend(input);
  };

  const handleVoiceRecording = () => {
    if (listening) {
      SpeechRecognition.stopListening();
      // After stopping, if there's a transcript, send it
      if (transcript.trim() !== '') {
        handleSend(transcript);
      }
    } else {
      resetTranscript();
      SpeechRecognition.startListening({ continuous: false }); // continuous: false is often more reliable
    }
  };

  // Check for browser support
  if (!browserSupportsSpeechRecognition) {
    return <span>Sorry, your browser does not support speech recognition.</span>;
  }

  return (
    <div className="App">
      <header className="App-header">
        <img src="/logo.png" className="App-logo" alt="logo" />
        <h1>BNI-ChatBot</h1>
      </header>
      <div className="chat-window">
        {messages.map((msg, index) => (
          <div key={index} className={`message ${msg.sender}`}>
            {msg.sender === 'bot' ? (
              <ReactMarkdown>{msg.text}</ReactMarkdown>
            ) : (
              <p>{msg.text}</p>
            )}
          </div>
        ))}
      </div>
      <div className="chat-input-container">
        <div className="chat-input">
          <textarea
            value={input}
            onChange={(e) => setInput(e.target.value)}
            placeholder={listening ? "Listening..." : "Ask a business-related query..."}
            rows="1"
            onKeyPress={(e) => {
              if (e.key === 'Enter' && !e.shiftKey) {
                e.preventDefault(); // Prevents new line on Enter
                handleTextSubmit();
              }
            }}
          />
          <button 
            onClick={handleVoiceRecording} 
            className={`mic-button ${listening ? 'listening' : ''}`}
          >
            ðŸŽ¤
          </button>
          <button onClick={handleTextSubmit} className="send-button">â†‘</button>
        </div>
      </div>
    </div>
  );
}

export default App;
