import React, { useState, useEffect, useRef } from 'react';
import ReactMarkdown from 'react-markdown';
import SpeechRecognition, { useSpeechRecognition } from 'react-speech-recognition';
import './App.css';

// --- SVG Icons for Messages ---
const UserIcon = () => (
  <svg width="30" height="30" viewBox="0 0 24 24" fill="#d9d9e9" xmlns="http://www.w3.org/2000/svg">
    <path d="M12 12c2.21 0 4-1.79 4-4s-1.79-4-4-4-4 1.79-4 4 1.79 4 4 4zm0 2c-2.67 0-8 1.34-8 4v2h16v-2c0-2.66-5.33-4-8-4z"/>
  </svg>
);

const BotIcon = ({ logoSrc }) => (
  <img src={logoSrc} alt="bot icon" style={{ width: '30px', height: '30px', borderRadius: '4px' }} />
);

// 3. New SVG Icon for the microphone
const MicrophoneIcon = () => (
  <svg width="24" height="24" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg" stroke="currentColor" strokeWidth="1.5" strokeLinecap="round" strokeLinejoin="round">
    <path d="M12 1a3 3 0 0 0-3 3v8a3 3 0 0 0 6 0V4a3 3 0 0 0-3-3z"></path>
    <path d="M19 10v2a7 7 0 0 1-14 0v-2"></path>
    <line x1="12" y1="19" x2="12" y2="22"></line>
  </svg>
);


function App() {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const chatWindowRef = useRef(null);

  const { transcript, listening, resetTranscript, browserSupportsSpeechRecognition } = useSpeechRecognition();

  useEffect(() => {
    window.speechSynthesis.getVoices();
  }, []);

  useEffect(() => {
    setInput(transcript);
  }, [transcript]);

  useEffect(() => {
    if (chatWindowRef.current) {
      chatWindowRef.current.scrollTop = chatWindowRef.current.scrollHeight;
    }
  }, [messages]);

  // 1. Function to clear the chat
  const handleNewChat = () => {
    setMessages([]);
    resetTranscript();
    setInput('');
  };

  const handleSend = async (messageToSend) => {
    if (messageToSend.trim() === '') return;

    setInput('');
    resetTranscript();

    const userMessage = { text: messageToSend, sender: 'user' };
    setMessages(prevMessages => [...prevMessages, userMessage]);

    try {
      const response = await fetch('http://localhost:5001/api/chat', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ message: messageToSend }),
      });

      const data = await response.json();
      const botMessage = { text: data.reply, sender: 'bot' };
      setMessages(prevMessages => [...prevMessages, botMessage]);

      window.speechSynthesis.cancel();
      const utterance = new SpeechSynthesisUtterance(data.reply);
      window.speechSynthesis.speak(utterance);

    } catch (error) {
      console.error('Error fetching bot reply:', error);
      const errorMessage = { text: 'Sorry, I am having trouble connecting.', sender: 'bot' };
      setMessages(prevMessages => [...prevMessages, errorMessage]);
    }
  };

  const handleTextSubmit = () => {
    if (input.trim() === '') return;
    handleSend(input);
  };

  const handleVoiceRecording = () => {
    if (listening) {
      SpeechRecognition.stopListening();
      if (transcript.trim() !== '') {
        handleSend(transcript);
      }
    } else {
      resetTranscript();
      SpeechRecognition.startListening();
    }
  };

  if (!browserSupportsSpeechRecognition) {
    return <span>Sorry, your browser does not support speech recognition.</span>;
  }

  return (
    <div className="App">
      <div className="sidebar">
        {/* Added onClick handler here */}
        <div className="new-chat-button" onClick={handleNewChat}>
          + New Chat
        </div>
      </div>

      <div className="main-content">
        <div className="chat-window" ref={chatWindowRef}>
          {messages.length === 0 ? (
            <div className="welcome-screen">
              {/* 2. Standardized the logo source */}
              <img src="/logo.p" className="welcome-logo" alt="logo" />
              <h2>How can I help you today?</h2>
            </div>
          ) : (
            messages.map((msg, index) => (
              <div key={index} className={`message-wrapper ${msg.sender}`}>
                <div className="message">
                  <div className="message-icon">
                    {/* 2. Standardized the bot icon source */}
                    {msg.sender === 'user' ? <UserIcon /> : <BotIcon logoSrc="/logo.png" />}
                  </div>
                  <div className="message-content">
                    <ReactMarkdown>{msg.text}</ReactMarkdown>
                  </div>
                </div>
              </div>
            ))
          )}
        </div>

        <div className="chat-input-container">
          <div className="chat-input">
            <textarea
              value={input}
              onChange={(e) => setInput(e.target.value)}
              placeholder={listening ? "Listening..." : "Ask BNI-ChatBot..."}
              onKeyPress={(e) => {
                if (e.key === 'Enter' && !e.shiftKey) {
                  e.preventDefault();
                  handleTextSubmit();
                }
              }}
            />
            <button 
              onClick={handleVoiceRecording} 
              className={`mic-button ${listening ? 'listening' : ''}`}
            >
              {/* 3. Replaced emoji with SVG component */}
              <MicrophoneIcon />
            </button>
            <button onClick={handleTextSubmit} className="send-button" disabled={!input.trim()}>
              ↑
            </button>
          </div>
          <p className="footer-text">BNI-ChatBot can make mistakes. Consider checking important information.</p>
        </div>
      </div>
    </div>
  );
}

export default App;
